log.info response
def count = context.expand('${JDBC_DatabaseConnection#ResponseAsXml#count(//ResultSet/Row/CUSTOMER.EMAIL)}').toInteger()
log.info count
for (def i=1;i<=count;i++)
{
	//log.info "$i"
	Email=context.expand('${JDBC_DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMER.EMAIL}')
	FirstName=context.expand('${JDBC_DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMER.FIRSTNAME}')
	LastName=context.expand('${JDBC_DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/CUSTOMER.LASTNAME}')
	log.info " Email: $Email"
	log.info " User Name: $FirstName"
	log.info " Last Name: $LastName"

